import React, { Component } from 'react';
import UserService from '../services/UserService';
import {BsEyeFill,BsFillPencilFill,BsFillPlusSquareFill,BsFillTrashFill} from 'react-icons/bs';
import { Link } from "react-router-dom";

class ListUserComponent extends Component {
    constructor(props){
        super(props)

        this.state = {
            users: []
        }
        this.addUser = this.addUser.bind(this);

    }

    componentDidMount(){
        UserService.getEmployees().then((res) => {
            this.setState({ users : res.data});
        });
    }

    addUser() {
        this.props.history.push('/add-users');    
    }

    render() {
        return (
            <div>
                <h2 className='text-center'>Users List</h2>
                {/* <div className='row'>
                    <button className='btn btn-primary' onClick={this.addUser}> Add User </button>
                </div> */}
                <div>
                    <Link to="/add-users" className="btn btn-primary">
                    <h3 className='text-center'>Add Users</h3>
                        <BsFillPlusSquareFill/>
                    </Link>
                </div>
                <div className='row'>
                    <table className='table table-striped table-bordered'>
                        <thead>
                            <tr>
                                <th>UserId</th>
                                <th>Name</th>
                                <th>EmailId</th>
                                <th>UserName</th>
                                <th>Password</th>
                                <th>Role</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.users.map(
                                    user =>
                                    <tr key = {user.userId}>
                                        <td> {user.userId} </td>
                                        <td> {user.name} </td>
                                        <td> {user.email} </td>
                                        <td> {user.username} </td>
                                        <td> {user.password} </td>
                                        <td> {user.role} </td>
                                    </tr>
                                )
                            }



                        </tbody>
                    </table>

                </div>

            </div>
        );
    }
}

export default ListUserComponent;
